    const querySnapshot =  getDocs(collection(db, "users"));
    querySnapshot.forEach((doc) => {
      console.log(`${doc.id} => ${doc.data()}`);
    });

  }

  return (
    <div className="todo-container">
      <h1>Test</h1>
      <form>
        <TextField onChange={(e) => {
          setTodoInput(e.target.value);

        }}
        className='text-field' id='outlined-basic' label='Write a Todo' variant='outlined' />
        <Button type='submit' onClick={addTodo} color='secondary' variant="contained">Contained</Button>
      </form>
    </div>
  );
}

export default App;
